import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const isSafe = (grid, row, col, num) => {
  if (grid[row][col] !== 0) return false;
  for (let i = 0; i < 9; i++) {
    if (grid[row][i] === num || grid[i][col] === num) return false;
    const boxRow = 3 * Math.floor(row / 3) + Math.floor(i / 3);
    const boxCol = 3 * Math.floor(col / 3) + i % 3;
    if (grid[boxRow][boxCol] === num) return false;
  }
  return true;
};

const solveSudoku = (grid) => {
  const isValid = (row, col, num) => {
    for (let i = 0; i < 9; i++) {
      if (grid[row][i] === num || grid[i][col] === num) return false;
      const boxRow = 3 * Math.floor(row / 3) + Math.floor(i / 3);
      const boxCol = 3 * Math.floor(col / 3) + i % 3;
      if (grid[boxRow][boxCol] === num) return false;
    }
    return true;
  };

  const solve = () => {
    for (let row = 0; row < 9; row++) {
      for (let col = 0; col < 9; col++) {
        if (grid[row][col] === 0) {
          for (let num = 1; num <= 9; num++) {
            if (isValid(row, col, num)) {
              grid[row][col] = num;
              if (solve()) return true;
              grid[row][col] = 0;
            }
          }
          return false;
        }
      }
    }
    return true;
  };

  return solve() ? grid : null;
};

const generateSudoku = (difficulty = "easy") => {
  const puzzle = Array(9)
    .fill(0)
    .map(() => Array(9).fill(0));

  let attempts;
  if (difficulty === "easy") attempts = 30;
  else if (difficulty === "medium") attempts = 20;
  else attempts = 10; // hard

  while (attempts > 0) {
    const row = Math.floor(Math.random() * 9);
    const col = Math.floor(Math.random() * 9);
    const num = Math.floor(Math.random() * 9) + 1;
    if (isSafe(puzzle, row, col, num)) {
      puzzle[row][col] = num;
      attempts--;
    }
  }
  const fixedCells = puzzle.map(row => row.map(val => val !== 0));
  return { puzzle, fixedCells };
};

function App() {
  const initial = generateSudoku();
  const [grid, setGrid] = useState(initial.puzzle);
  const [fixedCells, setFixedCells] = useState(initial.fixedCells);
  const [hintsLeft, setHintsLeft] = useState(3);
  const [difficulty, setDifficulty] = useState("easy");

  // Timer states
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const timerRef = useRef(null);

  useEffect(() => {
    if (isRunning) {
      timerRef.current = setInterval(() => {
        setTime(prev => prev + 1);
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    };
  }, [isRunning]);

  useEffect(() => {
    setTime(0);
    setIsRunning(false);
  }, [grid, difficulty]);

  const handleChange = (e, row, col) => {
    if (fixedCells[row][col]) return;

    const val = e.target.value;
    if (val === "" || (/^[1-9]$/.test(val) && val.length === 1)) {
      const newGrid = grid.map(r => r.slice());
      newGrid[row][col] = val === "" ? 0 : parseInt(val);
      setGrid(newGrid);
    }
  };

  const loadNewPuzzle = () => {
    const newPuzzle = generateSudoku(difficulty);
    setGrid(newPuzzle.puzzle);
    setFixedCells(newPuzzle.fixedCells);
    setHintsLeft(3);
  };

  const loadBlankPuzzle = () => {
    const blankGrid = Array(9)
      .fill(0)
      .map(() => Array(9).fill(0));
    const blankFixed = Array(9)
      .fill(0)
      .map(() => Array(9).fill(false));
    setGrid(blankGrid);
    setFixedCells(blankFixed);
    setHintsLeft(3);
  };

  const solveCurrentPuzzle = () => {
    const copyGrid = grid.map(r => r.slice());
    const solved = solveSudoku(copyGrid);
    if (solved) setGrid(solved);
    else alert("No solution found!");
  };

  const giveHint = () => {
    if (hintsLeft === 0) {
      alert("No hints left!");
      return;
    }
    const copyGrid = grid.map(r => r.slice());
    const solved = solveSudoku(copyGrid);
    if (!solved) {
      alert("No solution found to provide hints!");
      return;
    }
    for (let row = 0; row < 9; row++) {
      for (let col = 0; col < 9; col++) {
        if (grid[row][col] === 0) {
          const newGrid = grid.map(r => r.slice());
          newGrid[row][col] = solved[row][col];
          setGrid(newGrid);
          setHintsLeft(hintsLeft - 1);
          return;
        }
      }
    }
    alert("No empty cells to give hints!");
  };

  const formatTime = (seconds) => {
    const m = Math.floor(seconds / 60).toString().padStart(2, "0");
    const s = (seconds % 60).toString().padStart(2, "0");
    return `${m}:${s}`;
  };

  const isCellWrong = (row, col) => {
    if (grid[row][col] === 0) return false;
    const val = grid[row][col];
    for (let i = 0; i < 9; i++) {
      if (i !== col && grid[row][i] === val) return true;
      if (i !== row && grid[i][col] === val) return true;
    }
    const boxRow = 3 * Math.floor(row / 3);
    const boxCol = 3 * Math.floor(col / 3);
    for (let r = boxRow; r < boxRow + 3; r++) {
      for (let c = boxCol; c < boxCol + 3; c++) {
        if ((r !== row || c !== col) && grid[r][c] === val) return true;
      }
    }
    return false;
  };

  return (
    <div className="app">
      <h1>Sudoku</h1>

      {/* Container with flex */}
      <div style={{ display: "flex", gap: "30px" }}>
        {/* Sudoku grid on the left */}
        <div>
          <div className="sudoku-grid" style={{ marginTop: "20px" }}>
            {grid.map((row, rIdx) =>
              row.map((val, cIdx) => {
                const isFixed = fixedCells[rIdx][cIdx];
                const wrong = !isFixed && isCellWrong(rIdx, cIdx);

                const extraBorders = [
                  rIdx % 3 === 0 ? "thick-top" : "",
                  cIdx % 3 === 0 ? "thick-left" : "",
                  rIdx === 8 ? "thick-bottom" : "",
                  cIdx === 8 ? "thick-right" : ""
                ].join(" ");

                return (
                  <input
                    key={`${rIdx}-${cIdx}`}
                    className={`cell ${isFixed ? "fixed" : ""} ${extraBorders} ${wrong ? "wrong" : ""}`}
                    maxLength="1"
                    value={val === 0 ? "" : val}
                    onChange={(e) => handleChange(e, rIdx, cIdx)}
                    type="text"
                    inputMode="numeric"
                    pattern="[1-9]*"
                    disabled={isFixed}
                    autoComplete="off"
                  />
                );
              })
            )}
          </div>
        </div>

        {/* Controls on the right */}
        <div style={{ display: "flex", flexDirection: "column", gap: "20px", minWidth: "200px" }}>
          <div>
            <label htmlFor="difficulty-select">Select Difficulty: </label>
            <select
              id="difficulty-select"
              value={difficulty}
              onChange={e => {
                setDifficulty(e.target.value);
                const newPuzzle = generateSudoku(e.target.value);
                setGrid(newPuzzle.puzzle);
                setFixedCells(newPuzzle.fixedCells);
                setHintsLeft(3);
              }}
            >
              <option value="easy">Easy</option>
              <option value="medium">Medium</option>
              <option value="hard">Hard</option>
            </select>
          </div>

          <div className="buttons" style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            <button onClick={loadNewPuzzle}>New Puzzle</button>
            <button onClick={loadBlankPuzzle}>Load Blank Puzzle</button>
            <button onClick={giveHint}>Hint ({hintsLeft} left)</button>
            <button onClick={solveCurrentPuzzle}>Solve</button>
          </div>

          <div>
            <div>Timer: {formatTime(time)}</div>
            <button onClick={() => setIsRunning(true)} disabled={isRunning} style={{ marginTop: "10px" }}>
              Start Timer
            </button>
            <button onClick={() => setIsRunning(false)} disabled={!isRunning} style={{ marginTop: "5px" }}>
              Stop Timer
            </button>
            <button
              onClick={() => {
                setTime(0);
                setIsRunning(false);
              }}
              style={{ marginTop: "5px" }}
            >
              Reset Timer
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
