import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const isSafe = (grid, row, col, num) => {
  if (grid[row][col] !== 0) return false;
  for (let i = 0; i < 9; i++) {
    if (grid[row][i] === num || grid[i][col] === num) return false;
    const boxRow = 3 * Math.floor(row / 3) + Math.floor(i / 3);
    const boxCol = 3 * Math.floor(col / 3) + i % 3;
    if (grid[boxRow][boxCol] === num) return false;
  }
  return true;
};

const solveSudoku = (grid) => {
  const isValid = (row, col, num) => {
    for (let i = 0; i < 9; i++) {
      if (grid[row][i] === num || grid[i][col] === num) return false;
      const boxRow = 3 * Math.floor(row / 3) + Math.floor(i / 3);
      const boxCol = 3 * Math.floor(col / 3) + i % 3;
      if (grid[boxRow][boxCol] === num) return false;
    }
    return true;
  };

  const solve = () => {
    for (let row = 0; row < 9; row++) {
      for (let col = 0; col < 9; col++) {
        if (grid[row][col] === 0) {
          for (let num = 1; num <= 9; num++) {
            if (isValid(row, col, num)) {
              grid[row][col] = num;
              if (solve()) return true;
              grid[row][col] = 0;
            }
          }
          return false;
        }
      }
    }
    return true;
  };

  return solve() ? grid : null;
};

const generateSudoku = (difficulty = "medium") => {
  const puzzle = Array(9).fill(0).map(() => Array(9).fill(0));
  let attempts = 0;

  // Number of cells to fill based on difficulty
  const fillCount = difficulty === "easy" ? 30 : difficulty === "medium" ? 20 : 15;

  while (attempts < fillCount) {
    const row = Math.floor(Math.random() * 9);
    const col = Math.floor(Math.random() * 9);
    const num = Math.floor(Math.random() * 9) + 1;
    if (isSafe(puzzle, row, col, num)) {
      puzzle[row][col] = num;
      attempts++;
    }
  }
  const fixedCells = puzzle.map(row => row.map(val => val !== 0));
  return { puzzle, fixedCells };
};

const isCellValid = (grid, row, col, num) => {
  for (let i = 0; i < 9; i++) {
    if (i !== col && grid[row][i] === num) return false;
    if (i !== row && grid[i][col] === num) return false;
    const boxRow = 3 * Math.floor(row / 3) + Math.floor(i / 3);
    const boxCol = 3 * Math.floor(col / 3) + i % 3;
    if ((boxRow !== row || boxCol !== col) && grid[boxRow][boxCol] === num) return false;
  }
  return true;
};

function App() {
  const [difficulty, setDifficulty] = useState("medium");
  const initial = generateSudoku(difficulty);
  const [grid, setGrid] = useState(initial.puzzle);
  const [fixedCells, setFixedCells] = useState(initial.fixedCells);
  const [hintsLeft, setHintsLeft] = useState(3);
  const [selectedCell, setSelectedCell] = useState({ row: 0, col: 0 });

  // Timer states
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const timerRef = useRef(null);

  useEffect(() => {
    if (isRunning) {
      timerRef.current = setInterval(() => {
        setTime(prev => prev + 1);
      }, 1000);
    } else if (timerRef.current) {
      clearInterval(timerRef.current);
    }
    return () => clearInterval(timerRef.current);
  }, [isRunning]);

  // Regenerate puzzle when difficulty changes
  useEffect(() => {
    const newPuzzle = generateSudoku(difficulty);
    setGrid(newPuzzle.puzzle);
    setFixedCells(newPuzzle.fixedCells);
    setHintsLeft(3);
    setSelectedCell({ row: 0, col: 0 });
    setTime(0);
    setIsRunning(true);
  }, [difficulty]);



  const loadNewPuzzle = () => {
    const newPuzzle = generateSudoku(difficulty);
    setGrid(newPuzzle.puzzle);
    setFixedCells(newPuzzle.fixedCells);
    setHintsLeft(3);
    setSelectedCell({ row: 0, col: 0 });
    setTime(0);
    setIsRunning(true);
  };

  const loadBlankPuzzle = () => {
    const blankGrid = Array(9).fill(0).map(() => Array(9).fill(0));
    const blankFixed = Array(9).fill(0).map(() => Array(9).fill(false));
    setGrid(blankGrid);
    setFixedCells(blankFixed);
    setHintsLeft(3);
    setSelectedCell({ row: 0, col: 0 });
    setTime(0);
    setIsRunning(false);
  };

  const solveCurrentPuzzle = () => {
    const copyGrid = grid.map(r => r.slice());
    const solved = solveSudoku(copyGrid);
    if (solved) {
      setGrid(solved);
      setIsRunning(false);
    } else {
      alert("No solution found!");
    }
  };

  const giveHint = () => {
    if (hintsLeft === 0) {
      alert("No hints left!");
      return;
    }

    const copyGrid = grid.map(r => r.slice());
    const solved = solveSudoku(copyGrid);

    if (!solved) {
      alert("No solution found to provide hints!");
      return;
    }

    for (let row = 0; row < 9; row++) {
      for (let col = 0; col < 9; col++) {
        if (grid[row][col] === 0) {
          const newGrid = grid.map(r => r.slice());
          newGrid[row][col] = solved[row][col];
          setGrid(newGrid);
          setHintsLeft(hintsLeft - 1);
          return;
        }
      }
    }
    alert("No empty cells to give hints!");
  };

  // Arrow key navigation for selected cell
  const handleKeyDown = (e) => {
    e.preventDefault();
    const { row, col } = selectedCell;
    switch (e.key) {
      case "ArrowUp":
        setSelectedCell({ row: (row + 8) % 9, col });
        break;
      case "ArrowDown":
        setSelectedCell({ row: (row + 1) % 9, col });
        break;
      case "ArrowLeft":
        setSelectedCell({ row, col: (col + 8) % 9 });
        break;
      case "ArrowRight":
        setSelectedCell({ row, col: (col + 1) % 9 });
        break;
      default:
        break;
    }
  };

  // Format timer as mm:ss
  const formatTime = (seconds) => {
    const m = Math.floor(seconds / 60).toString().padStart(2, "0");
    const s = (seconds % 60).toString().padStart(2, "0");
    return `${m}:${s}`;
  };

  return (
    <div
      className="app"
      tabIndex={0}
      onKeyDown={handleKeyDown}
      style={{ outline: "none" }}
    >
      <h1>Sudoku</h1>

      <label>
        Difficulty:&nbsp;
        <select
          value={difficulty}
          onChange={(e) => setDifficulty(e.target.value)}
        >
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
      </label>

      <div style={{ marginTop: "1rem", fontWeight: "bold" }}>
        Time: {formatTime(time)}
      </div>

      <div style={{ margin: "0.5rem 0" }}>
        {isRunning ? (
          <button onClick={() => setIsRunning(false)}>Stop Timer</button>
        ) : (
          <button onClick={() => setIsRunning(true)}>Start Timer</button>
        )}
      </div>

      <div className="sudoku-grid">
        {grid.map((row, rIdx) =>
          row.map((val, cIdx) => {
            const isFixed = fixedCells[rIdx][cIdx];
            const isSelected = selectedCell.row === rIdx && selectedCell.col === cIdx;

            const isValidCell =
              val === 0 || isCellValid(grid, rIdx, cIdx, val);

            // Determine border classes based on position
            const extraBorders = [
              rIdx % 3 === 0 ? "thick-top" : "",
              cIdx % 3 === 0 ? "thick-left" : "",
              rIdx === 8 ? "thick-bottom" : "",
              cIdx === 8 ? "thick-right" : ""
            ].join(" ");

            return (
              <input
                key={`${rIdx}-${cIdx}`}
                className={`cell 
                  ${isFixed ? "fixed" : ""}
                  ${extraBorders}
                  ${!isValidCell ? "invalid" : ""}
                  ${isSelected ? "selected" : ""}
                `}
                maxLength="1"
                value={val === 0 ? "" : val}
                onChange={(e) => handleChange(e, rIdx, cIdx)}
                type="text"
                inputMode="numeric"
                pattern="[1-9]*"
                disabled={isFixed}
                onClick={() => setSelectedCell({ row: rIdx, col: cIdx })}
              />
            );
          })
        )}
      </div>

      <div className="buttons">
        <button onClick={loadNewPuzzle}>New Puzzle</button>
        <button onClick={loadBlankPuzzle}>Load Blank Puzzle</button>
        <button onClick={giveHint}>Hint ({hintsLeft} left)</button>
        <button onClick={solveCurrentPuzzle}>Solve</button>
      </div>
    </div>
  );
}

export default App;
